mapper.select:
select
business_date, entrust_serial_no, batch_serial_no, report_serial_no, company_id, fund_id, confirm_codeno,
asset_id, combi_id, market_no, report_code, inter_code, bind_seat, stockholder_id, trade_seat, invest_type,
entrust_direction, report_direction, entrust_price_type, entrust_price, entrust_amount, entrust_balance,
cancel_amount, busin_amount, busin_balance, total_deal_amount, total_deal_balance, prebuy_frozen_balance,
entrust_status, entrust_launch_type, busin_times, entrust_time, last_deal_time, operator_no, revoke_cause,
report_serial_no_branch, rec_num, tran_reff, trade_platform_id, cancel_flag, cancel_serial_no,
append_entrust_xh_flag, mac, ip_address, volserial_no, ws_cpu, scheme_code, scheme_ins_code, algo_ordid, ins_id,
index_daily_modify, ins_stock_id, capital_account_no, branch_code, sys_branch_code, trade_interface_type,
arbitrage_serial_no, engaged_no, rival_seat, rival_stockholder_id, sys_date, sys_time, external_ordid,
third_remark, vote_senate_no, terminal_info_slit_ext, order_id, dfd_status, report_status, purchasing_code
from jy_tentrusts


        and business_date = #{businessDate}


        and entrust_serial_no = #{entrustSerialNo}


        and batch_serial_no = #{batchSerialNo}


        and report_serial_no = #{reportSerialNo}


        and company_id = #{companyId}


        and fund_id = #{fundId}


        and confirm_codeno = #{confirmCodeno}


        and asset_id = #{assetId}


        and combi_id = #{combiId}


        and market_no = #{marketNo}


        and report_code = #{reportCode}


        and inter_code = #{interCode}


        and bind_seat = #{bindSeat}


        and stockholder_id = #{stockholderId}


        and trade_seat = #{tradeSeat}


        and invest_type = #{investType}


        and entrust_direction = #{entrustDirection}


        and report_direction = #{reportDirection}


        and entrust_price_type = #{entrustPriceType}


        and entrust_price = #{entrustPrice}


        and entrust_amount = #{entrustAmount}


        and entrust_balance = #{entrustBalance}


        and cancel_amount = #{cancelAmount}


        and busin_amount = #{businAmount}


        and busin_balance = #{businBalance}


        and total_deal_amount = #{totalDealAmount}


        and total_deal_balance = #{totalDealBalance}


        and prebuy_frozen_balance = #{prebuyFrozenBalance}


        and entrust_status = #{entrustStatus}


        and entrust_launch_type = #{entrustLaunchType}


        and busin_times = #{businTimes}


        and entrust_time = #{entrustTime}


        and last_deal_time = #{lastDealTime}


        and operator_no = #{operatorNo}


        and revoke_cause = #{revokeCause}


        and report_serial_no_branch = #{reportSerialNoBranch}


        and rec_num = #{recNum}


        and tran_reff = #{tranReff}


        and trade_platform_id = #{tradePlatformId}


        and cancel_flag = #{cancelFlag}


        and cancel_serial_no = #{cancelSerialNo}


        and append_entrust_xh_flag = #{appendEntrustXhFlag}


        and mac = #{mac}


        and ip_address = #{ipAddress}


        and volserial_no = #{volserialNo}


        and ws_cpu = #{wsCpu}


        and scheme_code = #{schemeCode}


        and scheme_ins_code = #{schemeInsCode}


        and algo_ordid = #{algoOrdid}


        and ins_id = #{insId}


        and index_daily_modify = #{indexDailyModify}


        and ins_stock_id = #{insStockId}


        and capital_account_no = #{capitalAccountNo}


        and branch_code = #{branchCode}


        and sys_branch_code = #{sysBranchCode}


        and trade_interface_type = #{tradeInterfaceType}


        and arbitrage_serial_no = #{arbitrageSerialNo}


        and engaged_no = #{engagedNo}


        and rival_seat = #{rivalSeat}


        and rival_stockholder_id = #{rivalStockholderId}


        and sys_date = #{sysDate}


        and sys_time = #{sysTime}


        and external_ordid = #{externalOrdid}


        and third_remark = #{thirdRemark}


        and vote_senate_no = #{voteSenateNo}


        and terminal_info_slit_ext = #{terminalInfoSlitExt}


        and order_id = #{orderId}


        and dfd_status = #{dfdStatus}


        and report_status = #{reportStatus}


        and purchasing_code = #{purchasingCode}


limit #{pageable.offset}, #{pageable.pageSize}

